nx.draw_networkx_nodes(G, pos, node_size=700, node_color="lightblue")

# Draw edges
nx.draw_networkx_edges(G, pos, edgelist=G.edges(), edge_color="gray", width=2)
valid_nodes = {node for u, v, data in G.edges(data=True) if "label" in data for node in (u, v)}

# Define node labels (only for nodes that have labels)
node_labels = {node: G.nodes[node].get("label") for node in valid_nodes if "label" in G.nodes[node]}

# Define edge labels (only for edges that have labels)
edge_labels = {(u, v): data["label"] for u, v, data in G.edges(data=True) if "label" in data}


nx.draw_networkx_labels(G, pos, labels=node_labels, font_size=8, font_color="black")
 
